// eslint-disable-next-line no-unused-vars
import { getAllSubsets } from '../Utilities/helpers.js';

export default class ChapterHandler {
  constructor() {
    this.currentChapter = 1;
    this.chapters = [
      new Chapter(
        (async () => {

