        false,
      ),
      new Chapter(
        async () => {

        },
        '4. Algorithms on Tree Decompositions',
        false,
      ),
      new Chapter(
        async () => {
          d3.select('#container')
            .append('div')
            .attr('id', 'graph-container');

          d3.select('#container')
            .append('div')
