            .attr('min', 0)
            .attr('max', 100)
            .attr('step', 'any')
            .attr('step', '1')
            .attr('value', 10)
            .attr('class', 'slider')
            .on('input', () => {
              const val = document.getElementById('vertices-slider').value;
              d3.select('#v').text(val);
            });

          sandboxSidebar
            .append('text')
            .text('Edges:     ')
            .append('span')
            .attr('id', 'e')
            .text('10');

          sandboxSidebar
            .append('input')
            .attr('id', 'edges-slider')
            .attr('type', 'range')
            .attr('min', 0)
            .attr('max', 100)
            .attr('step', 'any')
            .attr('step', '1')
            .attr('value', 10)
            .attr('class', 'slider')
            .on('input', () => {
              const val = document.getElementById('edges-slider').value;
              d3.select('#e').text(val);
            });

          sandboxSidebar.append('span')
            .text('replay')
            .attr('class', 'material-icons')
            .on('click', () => {
